mapper.select:
select from CBPM_CORRIDOR_PATH where 0=0
and PATH_UID_=#{pathId}
order by SORT_

mapper.insert:
update CBPM_CORRIDOR_PATH
ID_=#{id}, PATHPRI=#{pathPri}, MDIRECTION=#{mdirection}, CORHR1=#{corhr1}, CORHR2=#{corhr2}, CORHR3=#{corhr3}, CORHR4=#{corhr4}, CORHR5=#{corhr5}, CORHR6=#{corhr6}, CORHR7=#{corhr7}, CORHR8=#{corhr8}, CORHR9=#{corhr9}, CORHR10=#{corhr10},SORT_=#{sort}, PATH_UID_=#{pathUid}, PRICE_RATIOA=#{priceRatioA},PRICE_RATIOB=#{priceRatioB},TRANS_LOSS=#{transLoss}
where ID_=#{id} and PATH_UID_=#{pathUid}
