mapper.update:
UPDATE PF_SYS_CONFIG
ID_=#{id,jdbcType=VARCHAR}, KEY_=#{key,jdbcType=VARCHAR}, VALUE_=#{value,jdbcType=VARCHAR}, LAST_MODIFIER_ = #{lastModifier, jdbcType=VARCHAR}, LAST_MODIFIER_CN_ = #{lastModifierCn, jdbcType=VARCHAR}, LAST_MODIFIED_ = #{lastModified, jdbcType=TIMESTAMP}, REMARK_ = #{remark, jdbcType=VARCHAR}, TYPE_ = #{type,jdbcType=VARCHAR}
where ID_=#{id}
